import 'package:flutter/material.dart';import 'package:fw_manager/common/config.dart';import 'package:fw_manager/core/configuration/app_routes.dart';import 'package:fw_manager/networking/index.dart';import 'package:get/get.dart';class LoginController extends GetxController {  TextEditingController emailController = TextEditingController();  TextEditingController passwordController = TextEditingController();  bool isSlider = false;  bool isVisible = false;  bool isRemember = false;  void updateStatus() {    isVisible = !isVisible;    update();  }  onLoginButton() async {    var requestData = {      "email": emailController.text,      "password": passwordController.text,    };    print(emailController.text);    print(passwordController.text);    var resData = await apis.call( apiMethods.login,requestData,ApiType.post);    print(resData);    if (resData.isSuccess == true && resData.data != null) {      Get.toNamed(        AppRoutes.home,        arguments: emailController.text.toString(),      );    } else {      print("Login not found");    }    update();  }  onRemember() {    isRemember = !isRemember;    update();  }}