// ignore_for_file: must_call_super, prefer_typing_uninitialized_variablesimport 'package:fw_manager/core/configuration/app_routes.dart';import 'package:get/get.dart';class MultiOrdersController extends GetxController {  bool isOpenTap = false;  bool isOpenOrder = false;  String selectedOrder = "B2B Order";  List order = [    {      "order": "B2B Order",      "isActive": true,    },    {      "order": "B2C Order",      "isActive": false,    },  ];  List<dynamic> selectedOrderAddress = [    for (int i = 1; i < 11; i++)      {        "addressHeder": "Mira$i Medical Hall (K.D.)",        "addressDate": "Jun 9, 2022, 1:38:30 PM",        "personName": "R5-1467   (9999999999)",        "address": "R-5 203, Gopal Darshan Complex, Opp. Paras Police Chowki, Katargam, Surat.",        "vendor": "Vendor : ",        "vendorName": "Pharmadistributor@gmail.com",        "delete": false,        "shortNo": "14${i}5",        "_id": i,      },  ];  List resultList = [];  void runFilter(String enteredKeyword, String bindText) {    List results = [];    if (enteredKeyword.isEmpty) {      results = selectedOrderAddress;    } else {      results = selectedOrderAddress.where((record) => record[bindText].toLowerCase().contains(enteredKeyword.toLowerCase())).toList();    }    resultList = results;    update();  }  onChange(int i) {    for (int a = 0; a < order.length; a++) {      if (a == i) {        order[a]["isActive"] = true;        selectedOrder = order[a]["order"];      } else {        order[a]["isActive"] = false;      }    }    update();  }  onOpenTap() {    isOpenTap = !isOpenTap;    isOpenOrder = false;    resultList = [];    update();  }  onOpenOrder() {    isOpenOrder = !isOpenOrder;    isOpenTap = false;    resultList = [];    update();  }  onSelectedLocation() {    Get.toNamed(AppRoutes.selectedLocation);    update();  }  onMerge() {    Get.toNamed(AppRoutes.merge);    update();  }  onMap(){    Get.toNamed(AppRoutes.mapView);    update();  }}