import 'package:fw_manager/controller/login_controller.dart';import 'package:fw_manager/core/configuration/app_routes.dart';import 'package:get/get.dart';class SplashController extends GetxController {  String? version;  LoginController loginController = Get.put(LoginController());  @override  void onInit() {    Future.delayed(const Duration(seconds: 2), () {      if (loginController.logonData == true && loginController.logonData != 0) {        Get.toNamed(AppRoutes.login);      }else{        Get.toNamed(AppRoutes.home);      }    });    super.onInit();  }}