import 'package:flutter/material.dart';import 'package:fw_manager/controller/common_controller.dart';import 'package:get/get.dart';dynamic commonBottomSheet({  required BuildContext context,  required Widget widget,  dynamic margin,  dynamic height,}) {  CommonController _commonController = Get.find();  return showModalBottomSheet<void>(    context: context,    backgroundColor: Colors.transparent,    isScrollControlled: true,    enableDrag: false,    builder: (BuildContext context) {      return StatefulBuilder(        builder: (BuildContext context, StateSetter setState) {          return Container(            margin: EdgeInsets.only(top: margin ?? _commonController.statusBarHeight),            height: height,            decoration: const BoxDecoration(              color: Colors.white,              borderRadius: BorderRadius.only(                topLeft: Radius.circular(10),                topRight: Radius.circular(10),              ),            ),            padding: MediaQuery.of(context).viewInsets,            child: SizedBox(              child: widget,            ),          );        },      );    },  );}