import 'package:flutter/material.dart';import 'package:fw_manager/controller/app_controller.dart';Widget commonButton({  void Function()? onTap,  String? text,  dynamic height,  dynamic width,  dynamic color,}) {  return Card(    elevation: 5,    shape: const RoundedRectangleBorder(      borderRadius: BorderRadius.all(        Radius.circular(8),      ),    ),    child: GestureDetector(      onTap: onTap,      child: Container(        decoration: BoxDecoration(          // color: color ?? const Color(0xFF5CB85C),          color: color ?? AppController().appTheme.primary1,          borderRadius: const BorderRadius.all(            Radius.circular(8),          ),        ),        child: Container(          height: height ?? 45,          width: width,          alignment: Alignment.center,          child: Text(            text ?? '',            style: const TextStyle(              fontSize: 18,              fontWeight: FontWeight.w600,              color: Colors.white,            ),          ),        ),      ),    ),  );}