import 'package:flutter/material.dart';import 'package:fw_manager/core/theme/app_css.dart';class CommonCodDriverCard extends StatefulWidget {  final String? orderNo;  final String? addressDate;  final String? codAmount;  final String? collectedAmount;  final String? diffAmount;  final String? allottedDrivers;  final Color? cardColors;  const CommonCodDriverCard({    Key? key,    this.orderNo,    this.addressDate,    this.cardColors,    this.codAmount,    this.collectedAmount,    this.diffAmount,    this.allottedDrivers,  }) : super(key: key);  @override  State<CommonCodDriverCard> createState() => _CommonCodDriverCardState();}class _CommonCodDriverCardState extends State<CommonCodDriverCard> {  @override  Widget build(BuildContext context) {    return Card(      elevation: 1,      shape: RoundedRectangleBorder(        borderRadius: const BorderRadius.all(          Radius.circular(8),        ),        side: BorderSide(          color: Colors.blue.shade400,          width: 1,        ),      ),      color: widget.cardColors ?? Colors.white,      child: Padding(        padding: const EdgeInsets.symmetric(horizontal: 15, vertical: 5),        child: Column(          mainAxisSize: MainAxisSize.min,          crossAxisAlignment: CrossAxisAlignment.start,          children: [            Row(              children: [                Text(                  widget.orderNo ?? '',                  style: AppCss.h2,                ),                const Spacer(),                Column(                  crossAxisAlignment: CrossAxisAlignment.end,                  children: [                    Row(                      crossAxisAlignment: CrossAxisAlignment.center,                      children: [                        Text(                          "Locations : ",                          style: AppCss.body3.copyWith(color: Colors.black),                        ),                        Text(                          widget.allottedDrivers ?? '',                          style: AppCss.h3.copyWith(color: Colors.black),                        ),                      ],                    ),                    const SizedBox(height: 5),                    Text(                      widget.addressDate ?? '',                      style: AppCss.caption.copyWith(fontSize: 8),                    ),                  ],                )              ],            ),            const SizedBox(height: 15),            Row(              mainAxisAlignment: MainAxisAlignment.spaceBetween,              children: [                Column(                  crossAxisAlignment: CrossAxisAlignment.center,                  children: [                    Text(                      'COD AMT',                      style: AppCss.h3.copyWith(                        color: Colors.grey.shade800,                      ),                    ),                    const SizedBox(height: 5),                    Text(                      widget.codAmount ?? '',                      style: AppCss.h3.copyWith(                        color: Colors.red,                      ),                    ),                  ],                ),                const SizedBox(width: 5),                Column(                  crossAxisAlignment: CrossAxisAlignment.center,                  children: [                    Text(                      'AMT REC',                      style: AppCss.h3.copyWith(                        color: Colors.grey.shade800,                      ),                    ),                    const SizedBox(height: 5),                    Text(                      widget.collectedAmount ?? '',                      style: AppCss.h3.copyWith(                        color: Colors.green,                      ),                    ),                  ],                ),                const SizedBox(width: 5),                Column(                  crossAxisAlignment: CrossAxisAlignment.center,                  children: [                    Text(                      'DIFF AMT',                      style: AppCss.h3.copyWith(                        color: Colors.grey.shade800,                      ),                    ),                    const SizedBox(height: 5),                    Text(                      widget.diffAmount ?? '',                      style: AppCss.h3.copyWith(                        color: Colors.red,                      ),                    ),                  ],                ),              ],            ),          ],        ),      ),    );  }}