import 'package:flutter/material.dart';import 'package:fw_manager/core/theme/app_css.dart';import 'package:get/get.dart';class CommonCodVendorsCard extends StatefulWidget {  final Color? cardColors;  final String? personName;  final String? collectedAmount;  final void Function()? onTap;  const CommonCodVendorsCard({    Key? key,    this.cardColors,    this.personName,    this.collectedAmount,    this.onTap,  }) : super(key: key);  @override  State<CommonCodVendorsCard> createState() => _CommonCodVendorsCardState();}class _CommonCodVendorsCardState extends State<CommonCodVendorsCard> {  @override  Widget build(BuildContext context) {    return GestureDetector(      onTap: widget.onTap,      child: Card(        elevation: 1,        shape: RoundedRectangleBorder(          borderRadius: const BorderRadius.all(            Radius.circular(5),          ),          side: BorderSide(            color: Colors.blue.shade400,            width: 1,          ),        ),        color: widget.cardColors ?? Colors.white,        child: Row(          mainAxisAlignment: MainAxisAlignment.spaceBetween,          children: [            Text(              widget.personName ?? '',              style: AppCss.h3.copyWith(                fontWeight: FontWeight.normal,              ),            ),            Text(              widget.collectedAmount ?? '',              style: AppCss.h3,            ),          ],        ).paddingSymmetric(horizontal: 10,vertical: 15),      ),    );  }}