import 'package:flutter/material.dart';import 'package:fw_manager/core/theme/app_css.dart';import 'package:get/get.dart';class CommonMergeCard extends StatefulWidget {  final String? person;  final String? personData;  final String? shortNo;  final String? address;  final String? items;  final void Function()? onItems;  final void Function()? shortNumberAdd;  const CommonMergeCard({    Key? key,    this.person,    this.personData,    this.shortNo,    this.address,    this.items,    this.onItems,    this.shortNumberAdd,  }) : super(key: key);  @override  State<CommonMergeCard> createState() => _CommonMergeCardState();}class _CommonMergeCardState extends State<CommonMergeCard> {  @override  Widget build(BuildContext context) {    return Card(      elevation: 1,      shape: RoundedRectangleBorder(        borderRadius: const BorderRadius.all(          Radius.circular(8),        ),        side: BorderSide(          color: Colors.blue.shade400,          width: 1,        ),      ),      child: Column(        children: [          Row(            children: [              Expanded(                child: Text(                  widget.personData.toString(),                  style: AppCss.h3.copyWith(color: Colors.black),                ),              ),              TextButton(                onPressed: widget.shortNumberAdd,                child: Row(                  children: [                    Text(                      'Short: ',                      style: AppCss.h3,                    ),                    Text(                      widget.shortNo ?? '',                      style: AppCss.h2.copyWith(color: Colors.black),                    ),                  ],                ),              ),            ],          ),          Row(            children: [              Expanded(                child: RichText(                  text: TextSpan(                    text: "${widget.person}  ",                    style: AppCss.h3.copyWith(color: Colors.black),                    children: <TextSpan>[                      TextSpan(                        text: widget.address ?? "",                        style: AppCss.caption,                      ),                    ],                  ),                ),              ),              TextButton(                onPressed: widget.onItems,                child: Text(                  "${widget.items} Items",                  style: AppCss.h3.copyWith(color: Colors.blue),                ),              ),            ],          ),        ],      ).paddingOnly(left: 10, bottom: 10),    );  }}