import 'package:flutter/material.dart';import 'package:fw_manager/core/theme/app_css.dart';import 'package:get/get.dart';class CommonMergeCard extends StatefulWidget {  final String? person;  final String? personData;  final String? shortNo;  final String? address;  final String? items;  final void Function()? onItems;  final void Function()? shortNumberAdd;  const CommonMergeCard({    Key? key,    this.person,    this.personData,    this.shortNo,    this.address,    this.items,    this.onItems,    this.shortNumberAdd,  }) : super(key: key);  @override  State<CommonMergeCard> createState() => _CommonMergeCardState();}class _CommonMergeCardState extends State<CommonMergeCard> {  @override  Widget build(BuildContext context) {    return Card(      elevation: 1,      shape: RoundedRectangleBorder(        borderRadius: const BorderRadius.all(          Radius.circular(8),        ),        side: BorderSide(          color: Colors.blue.shade400,          width: 1,        ),      ),      child: Row(        mainAxisAlignment: MainAxisAlignment.spaceBetween,        crossAxisAlignment: CrossAxisAlignment.start,        children: [          Expanded(            child: Column(              crossAxisAlignment: CrossAxisAlignment.start,              children: [                RichText(                  overflow: TextOverflow.clip,                  textAlign: TextAlign.end,                  textDirection: TextDirection.rtl,                  softWrap: true,                  maxLines: 1,                  textScaleFactor: 1,                  text: TextSpan(                    text: widget.person ?? '',                    style: AppCss.h3.copyWith(color: Colors.black),                    children: <TextSpan>[                      TextSpan(                        text: "(" + widget.personData.toString() + ")",                        style: DefaultTextStyle.of(context).style,                      ),                    ],                  ),                ),                const SizedBox(height: 5),                Text(                  widget.address ?? "",                  style: AppCss.caption,                  textAlign: TextAlign.start,                ),              ],            ).paddingOnly(right: 15, left: 15, top: 10),          ),          Column(            crossAxisAlignment: CrossAxisAlignment.end,            children: [              TextButton(                onPressed: widget.shortNumberAdd,                child: Row(                  children: [                    Text(                      'ShortNo : ',                      style: AppCss.h3,                    ),                    Text(                      widget.shortNo ?? '',                      style: AppCss.h2.copyWith(color: Colors.black),                    ),                  ],                ),              ),              TextButton(                onPressed: widget.onItems,                child: Text(                  widget.items ?? "",                  style: AppCss.h3.copyWith(color: Colors.blue),                ),              ),            ],          ),        ],      ),    );  }}