import 'package:flutter/material.dart';import 'package:fw_manager/core/theme/app_css.dart';class CommonSettlementCard extends StatefulWidget {  final String? id;  final String? orderNo;  final String? place;  final String? dateTime;  const CommonSettlementCard({Key? key, this.id, this.orderNo, this.place, this.dateTime}) : super(key: key);  @override  State<CommonSettlementCard> createState() => _CommonSettlementCardState();}class _CommonSettlementCardState extends State<CommonSettlementCard> {  @override  Widget build(BuildContext context) {    return Card(      elevation: 1,      shape: RoundedRectangleBorder(        borderRadius: const BorderRadius.all(          Radius.circular(8),        ),        side: BorderSide(          color: Colors.blue.shade400,          width: 1,        ),      ),      color: Colors.white,      child: Padding(        padding: const EdgeInsets.symmetric(horizontal:10, vertical:10),        child: Column(          mainAxisSize: MainAxisSize.min,          crossAxisAlignment: CrossAxisAlignment.start,          children: [            Row(              mainAxisAlignment: MainAxisAlignment.spaceBetween,              children: [                Expanded(                  child: Text(                    widget.orderNo ?? '',                    style: AppCss.h1,                  ),                ),                Text(                  widget.id ?? '',                  style: AppCss.body3,                ),              ],            ),            const SizedBox(height: 5),            Text(              widget.place ?? '',              style: AppCss.body2,            ),            const SizedBox(height: 5),            Text(              widget.dateTime ?? '',              style: AppCss.caption,            ),          ],        ),      ),    );  }}