// ignore_for_file: non_constant_identifier_namesimport 'package:flutter/material.dart';Widget CustomButton({  dynamic onTap,  String? text,  dynamic height,  dynamic width,  IconData? icon,  dynamic vertical,  dynamic horizontal,  dynamic color,  dynamic context,  dynamic iconColor,  dynamic buttonColor,}) {  return Padding(    padding: EdgeInsets.symmetric(vertical: vertical ?? 10.0, horizontal: horizontal ?? 0.0),    child: GestureDetector(      onTap: onTap,      child: Container(        height: height ?? 50,        width: width ?? double.infinity,        decoration: BoxDecoration(            color: color ?? Theme.of(context).primaryColor,            borderRadius: const BorderRadius.all(Radius.circular(15)),            boxShadow: kElevationToShadow[1]),        child: icon == null            ? Center(                child: Text(                  text ?? '',                  style: const TextStyle(                    color: Colors.white,                  ),                ),              )            : Row(                mainAxisAlignment: MainAxisAlignment.center,                children: [                  Icon(                    icon,                    color: iconColor ?? Colors.black,                  ),                  const SizedBox(width: 10),                  Text(                    text ?? '',                    style: TextStyle(                      color: buttonColor ?? Colors.black,                      fontSize: 18,                      fontWeight: FontWeight.bold,                    ),                  ),                ],              ),      ),    ),  );}