import 'package:flutter/material.dart';import 'package:fw_manager/core/theme/index.dart';class OrderAddressCard extends StatefulWidget {  final String? addressHeder;  final dynamic addressHederfontSize;  final String? addressDate;  final String? personName;  final String? address;  final String? vendor;  final String? vendorName;  final Color? cardColors;  final Color? deleteIconColor;  final Color? deleteIconBoxColor;  final IconData? deleteIcon;  final bool? icon;  final void Function()? onTap;  const OrderAddressCard({    Key? key,    this.addressHeder,    this.addressDate,    this.personName,    this.address,    this.vendor,    this.vendorName,    this.onTap,    this.cardColors,    this.deleteIcon,    this.deleteIconColor,    this.icon,    this.deleteIconBoxColor,    this.addressHederfontSize,  }) : super(key: key);  @override  State<OrderAddressCard> createState() => _OrderAddressCardState();}class _OrderAddressCardState extends State<OrderAddressCard> {  @override  Widget build(BuildContext context) {    return Card(      elevation: 1,      shape: RoundedRectangleBorder(        borderRadius: const BorderRadius.all(Radius.circular(8)),        side: BorderSide(          color: Colors.blue.shade400,          width: 1,        ),      ),      color: widget.cardColors ?? Colors.white,      child: Stack(        children: [          Padding(            padding: const EdgeInsets.symmetric(horizontal: 15, vertical: 5),            child: Column(              mainAxisSize: MainAxisSize.min,              crossAxisAlignment: CrossAxisAlignment.start,              children: [                Row(                  mainAxisAlignment: MainAxisAlignment.spaceBetween,                  children: [                    Expanded(                      child: Text(                        widget.addressHeder ?? '',                        style: AppCss.h1.copyWith(fontSize: widget.addressHederfontSize),                      ),                    ),                    GestureDetector(                      onTap: widget.onTap,                      child: AnimatedContainer(                        duration: const Duration(milliseconds: 100),                        alignment: Alignment.center,                        padding: const EdgeInsets.symmetric(horizontal: 2, vertical: 2),                        decoration: widget.icon == true                            ? const BoxDecoration()                            : BoxDecoration(                                color: widget.deleteIconBoxColor,                                borderRadius: const BorderRadius.all(                                  Radius.circular(8),                                ),                              ),                        child: Icon(                          widget.deleteIcon,                          color: widget.icon == true ? widget.deleteIconColor ?? Colors.white : Colors.white,                        ),                      ),                    ),                  ],                ),                const SizedBox(height: 5),                Text(                  widget.personName ?? '',                  style: AppCss.h3,                ),                const SizedBox(height: 5),                RichText(                  overflow: TextOverflow.clip,                  textAlign: TextAlign.end,                  textDirection: TextDirection.rtl,                  softWrap: true,                  maxLines: 1,                  textScaleFactor: 1,                  text: TextSpan(                    text: widget.vendor ?? '',                    style: DefaultTextStyle.of(context).style,                    children: <TextSpan>[                      TextSpan(                        text: widget.vendorName ?? '',                        style: AppCss.h3.copyWith(color: Colors.black),                      ),                    ],                  ),                ),              ],            ),          ),          // Container(          //   height: Get.height * 0.09,          //   // width:double.infinity,          //   decoration: const BoxDecoration(          //     // color:Colors.white,          //     border: Border(          //       right: BorderSide(          //         color: Colors.black,          //         width: 4,          //         style: BorderStyle.solid,          //       ),          //     ),          //   ),          // ),        ],      ),    );  }}