import 'package:flutter/material.dart';import 'package:fw_manager/core/theme/index.dart';import 'package:get/get.dart';class SelectedOrderAddressCard extends StatefulWidget {  final String? addressHeder;  final String? addressDate;  final String? personName;  final String? address;  final String? vendor;  final String? vendorName;  final String? shortNo;  final String? billNo;  final dynamic addressHederfontSize;  final dynamic height;  final Color? cardColors;  final Color? deleteIconColor;  final IconData? deleteIcon;  final void Function()? onTap;  final void Function()? shortNumberAdd;  const SelectedOrderAddressCard({    Key? key,    this.addressHeder,    this.addressDate,    this.personName,    this.address,    this.vendor,    this.vendorName,    this.onTap,    this.cardColors,    this.deleteIcon,    this.deleteIconColor,    this.shortNo,    this.shortNumberAdd,    this.addressHederfontSize,    this.billNo,    this.height,  }) : super(key: key);  @override  State<SelectedOrderAddressCard> createState() => _SelectedOrderAddressCardState();}class _SelectedOrderAddressCardState extends State<SelectedOrderAddressCard> {  @override  Widget build(BuildContext context) {    return Card(      elevation: 1,      shape: RoundedRectangleBorder(        borderRadius: const BorderRadius.all(          Radius.circular(8),        ),        side: BorderSide(          color: Colors.blue.shade400,          width: 1,        ),      ),      color: widget.cardColors ?? Colors.white,      child: Padding(        padding: const EdgeInsets.symmetric(horizontal: 15, vertical: 5),        child: Column(          mainAxisSize: MainAxisSize.min,          crossAxisAlignment: CrossAxisAlignment.start,          children: [            Row(              mainAxisAlignment: MainAxisAlignment.spaceBetween,              children: [                Expanded(                  child: Text(                    widget.addressHeder ?? '',                    style: AppCss.h1.copyWith(fontSize: widget.addressHederfontSize),                  ).paddingOnly(bottom: widget.height ?? 0.0),                ),                GestureDetector(                  onTap: widget.onTap,                  child: AnimatedContainer(                    duration: const Duration(milliseconds: 100),                    alignment: Alignment.center,                    padding: const EdgeInsets.symmetric(horizontal: 2, vertical: 2),                    decoration: BoxDecoration(                      color: widget.deleteIconColor,                      borderRadius: const BorderRadius.all(                        Radius.circular(8),                      ),                    ),                    child: Icon(                      widget.deleteIcon,                      color: Colors.white,                    ),                  ),                ),              ],            ),            widget.addressDate != null                ? Text(                    widget.addressDate ?? '',                    style: AppCss.body3,                  ).paddingOnly(bottom: widget.height ?? 0.0)                : const Text(""),            Row(              children: [                Text(                  widget.personName ?? '',                  style: AppCss.h3,                ),                const Spacer(),                widget.billNo != null                    ? Row(                        children: [                          Text(                            'BillNo : ',                            style: AppCss.h3.copyWith(color: Theme.of(context).primaryColor),                          ),                          Text(                            widget.billNo ?? '',                            style: AppCss.h2.copyWith(color: Colors.black),                          ),                        ],                      )                    : const Text(""),                widget.shortNo != null                    ? TextButton(                        onPressed: widget.shortNumberAdd,                        child: Row(                          children: [                            Text(                              'ShortNo : ',                              style: AppCss.h3,                            ),                            Text(                              widget.shortNo ?? '',                              style: AppCss.h2.copyWith(color: Colors.black),                            ),                          ],                        ),                      )                    : const Text(""),              ],            ),            widget.address != null                ? Text(                    widget.address ?? '',                    style: AppCss.body3,                  ).paddingOnly(bottom: 5)                : const Text(""),            RichText(              overflow: TextOverflow.clip,              textAlign: TextAlign.end,              textDirection: TextDirection.rtl,              softWrap: true,              maxLines: 1,              textScaleFactor: 1,              text: TextSpan(                text: widget.vendor ?? '',                style: AppCss.h3.copyWith(color: Colors.black),                children: <TextSpan>[                  TextSpan(                    text: widget.vendorName ?? '',                    style: AppCss.h3.copyWith(color: Colors.black),                  ),                ],              ),            ),          ],        ),      ),    );  }}