// ignore_for_file: must_be_immutableimport 'package:flutter/material.dart';import 'package:fw_manager/controller/app_controller.dart';import 'package:fw_manager/controller/cod_controller.dart';import 'package:fw_manager/core/theme/app_css.dart';import 'package:fw_manager/core/widgets/cod_widgets/by_drivers_widgets.dart';import 'package:fw_manager/core/widgets/cod_widgets/by_vendor_widgets.dart';import 'package:get/get.dart';class CodSettlementScreen extends StatelessWidget {  CodSettlementController codSettlementController = Get.put(CodSettlementController());  CodSettlementScreen({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return GetBuilder<CodSettlementController>(      builder: (_) => Padding(        padding: const EdgeInsets.symmetric(horizontal: 10, vertical: 10),        child: Column(          children: [            Row(              mainAxisAlignment: MainAxisAlignment.spaceBetween,              children: [                for (int i = 0; i < codSettlementController.codList.length; i++)                  GestureDetector(                    onTap: () => codSettlementController.onChange(i),                    child: AnimatedContainer(                      height: 45,                      width: Get.width * 0.45,                      alignment: Alignment.center,                      duration: const Duration(milliseconds: 100),                      decoration: BoxDecoration(                        borderRadius: const BorderRadius.all(                          Radius.circular(8),                        ),                        border: Border.all(                          color:                              codSettlementController.codList[i]["isActive"] ? Colors.transparent : AppController().appTheme.primary1.withOpacity(.8),                          width: 1,                        ),                        color: codSettlementController.codList[i]["isActive"] ? AppController().appTheme.primary1.withOpacity(.8) : Colors.white,                      ),                      child: Text(                        codSettlementController.codList[i]["cod"],                        style: AppCss.h1.copyWith(                          color: codSettlementController.codList[i]["isActive"] ? Colors.white : AppController().appTheme.primary1.withOpacity(.8),                        ),                      ),                    ),                  ),              ],            ),            const SizedBox(height: 15),            if(codSettlementController.codList[0]["isActive"])            const Expanded(child: ByVendorWidgets()),            if(codSettlementController.codList[1]["isActive"])              const Expanded(child: ByDriversWidgets()),          ],        ),      ),    );  }}